# check
# –∫—Ä—É–≥–∏ + –∫–∞—Ç–µ–≥–æ—Ä–∏—è –¥—Ä—É–≥–∏–µ

# —Ñ—É–Ω–∫—Ü–∏—è –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç –Ω–∞ –≤–µ—Ä—Å–∏–∏ –±–∏–±–ª–∏–æ—Ç–µ–∫–∏ pandas >= 2.0 (–º–µ—Ç–æ–¥ .append —É—Å—Ç–∞—Ä–µ–ª)

def graph (df, year, region, name, axes):
    
# —Ñ–∏–ª—å—Ç—Ä—É–µ–º –≤—ã–±–æ—Ä–∫—É –ø–æ –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π

    df = df.query('year_of_release >= @year')

# —Å–æ–±–∏—Ä–∞–µ–º –¢–û–ü-5

    sales = df.pivot_table(index='platform', 
                           values=region, 
                           aggfunc='sum').nlargest(5, region)
    
    sales = sales.reset_index()

# –¥–æ–±–∞–≤–ª—è–µ–º 6-—é –∫–∞—Ç–µ–≥–æ—Ä–∏—é –≤ –¢–û–ü-5

    sales = (
            sales.append({'platform': 'Other', region: df[region].sum() 
                       - sales[region].sum()}, ignore_index= True)
         )
    
# –º–µ–Ω—è–µ–º –Ω–∞–∑–≤–∞–Ω–∏—è –∫–æ–ª–æ–Ω–æ–∫, –Ω–æ –Ω–µ–æ–ø—Ç–∏–º–∞–ª—å–Ω—ã–º —Å–ø–æ—Å–æ–±–æ–º ü§ù

    sales.columns = ['platform', 'sales']

# —Å–æ–∑–¥–∞–µ–º —Å–ª–æ–≤–∞—Ä—å —Ü–≤–µ—Ç–æ–≤    
# –¶–≤–µ—Ç–æ–≤–∞—è –≥–∞–º–º–∞ –Ω–µ –ø–æ–¥–±–∏—Ä–∞–ª–∞—Å—å —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ –ø–æ–¥ –ø–ª–∞—Ç—Ñ–æ—Ä–º—ã, –∫–æ–º—É-—á—Ç–æ –ø–æ–ø–∞–¥–µ—Ç—Å—è, üòâ 
      
    labels_c=sales.platform
    colours = {'Wii':'C60', 'NES':'C1', 'GB':'C2', 'DS':'C3', 'X360':'C4', 
    'PS3':'C5', 'PS2':'C6', 'SNES':'C7', 'GBA':'C8',
               'PS4':'lightsteelblue', '3DS':'orange', 
               'N64':'C11', 'PS':'C12', 'XB':'C13', 'PC':'Fuchsia', '2600':'C15', 'PSP':'C48', 
               'XOne':'LimeGreen',
               'WiiU':'C18', 'GC':'C19', 'GEN':'C20', 'DC':'C21', 'PSV':'C22', 
               'SAT':'C23', 'SCD':'C24', 'WS':'C25', 'NG':'C26', 
               'TG16':'C27', '3DO':'C28', 'GG':'C29', 'PCFX':'C30', 'Other':'darkred'}
   
# —Ä–∏—Å—É–µ–º –≥—Ä–∞—Ñ–∏–∫–∏

    sales.plot(kind='pie',
               y="sales",
               
               autopct='%1.0f%%',
               wedgeprops={'linewidth': 3.0, 'edgecolor': 'white'},
               textprops={'size': 'x-large'}, 
               labels= labels_c,
               colors=[colours[key] for key in labels_c],
               legend=False, 
               title = f"–ü–æ–ø—É–ª—è—Ä–Ω–æ—Å—Ç—å –ø–ª–∞—Ç—Ñ–æ—Ä–º –≤ {name} ", 
               ax = axes).set(ylabel='')
    
# —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≥—Ä–∞–Ω–∏—Ü—ã –¥–ª—è –≥—Ä–∞—Ñ–∏–∫–æ–≤, —á—Ç–æ–±—ã –Ω–µ –ø–µ—Ä–µ–∫—Ä—ã–≤–∞–ª–∏ –¥—Ä—É–≥ –¥—Ä—É–≥–∞
    
    plt.tight_layout()


